import React from 'react';

const Modal = ({ selectedImg, setSelectedImg }) => {
    const closeModal = (e) => {
        setSelectedImg = null;
    };
    return (
        <div onClick={(e) => closeModal} className="backdrop">
            <img src={selectedImg} alt="enlarged pic" />
        </div>
    );
};

export default Modal;
